coderExample:
// CREATING VARIABLES
int age = 25;  // int = whole numbers
string name = "Alice";  // string = text
double price = 19.99;  // double = numbers with decimals

// USING VARIABLES
Console.WriteLine(age);    // Displays: 25
Console.WriteLine(name);   // Displays: Alice
Console.WriteLine(price);  // Displays: 19.99

// CHANGING VALUES
age = 26;  // Age is now 26
Console.WriteLine(age);  // Displays: 26

// VARIABLES IN MESSAGES
Console.WriteLine("My name is " + name);
Console.WriteLine("I am " + age + " years old");

// STRING INTERPOLATION (easier way!)
Console.WriteLine($"My name is {name}");
Console.WriteLine($"I am {age} years old");
Console.WriteLine($"The price is ${price}");

