challenge.solutionCode:
// Personal Profile Program

string name = "Alex";
int age = 28;
int favoriteNumber = 7;
double height = 1.80;
string city = "Seattle";

Console.WriteLine(name);
Console.WriteLine(age);
Console.WriteLine(favoriteNumber);
Console.WriteLine(height);
Console.WriteLine(city);

Console.WriteLine($"Hi, I'm {name}, age {age}, from {city}");
Console.WriteLine($"I am {height}m tall and my favorite number is {favoriteNumber}");
