challenge.solutionCode:
// Game Character Profile

string characterName = "Knight";
int health = 100;
double attackPower = 15.5;
bool isAlive = true;
int level = 5;

Console.WriteLine($"Character: {characterName}");
Console.WriteLine($"Level: {level}");
Console.WriteLine($"Health: {health} HP");
Console.WriteLine($"Attack Power: {attackPower}");
Console.WriteLine($"Status: {(isAlive ? "Alive" : "Dead")}");

Console.WriteLine($"\n{characterName} (Level {level}): {health} HP, {attackPower} ATK, Status: {(isAlive ? "Alive" : "Dead")}");
