challenge.solutionCode:
// Currency Converter

// Define conversion rate constants
const double USD_TO_EUR = 0.85;
const double USD_TO_GBP = 0.73;
const double USD_TO_JPY = 110.0;
const string APP_NAME = "Currency Converter";
const string VERSION = "1.0";

// Display app info
Console.WriteLine($"{APP_NAME} v{VERSION}");
Console.WriteLine("=".PadLeft(30, '='));

// Get user input
Console.WriteLine("Enter amount in USD:");
double usd = double.Parse(Console.ReadLine());

// Perform conversions using constants
double eur = usd * USD_TO_EUR;
double gbp = usd * USD_TO_GBP;
double jpy = usd * USD_TO_JPY;

// Display results
Console.WriteLine($"\n{usd} USD = {eur} EUR");
Console.WriteLine($"{usd} USD = {gbp} GBP");
Console.WriteLine($"{usd} USD = {jpy} JPY");
