// Create your character status variables
bool isHungry = true;
bool isTired = false;
int energy = 75;
string name = "Hero";

// Display status report
Console.WriteLine("=== Character Status ===");
Console.WriteLine("Name: " + name);
Console.WriteLine("Energy: " + energy);
Console.WriteLine("Hungry: " + isHungry);
Console.WriteLine("Tired: " + isTired);
