// BASIC MATH CLASS METHODS

// POWER - Exponents
double squared = Math.Pow(5, 2);  // 5¬≤ = 25.0
double cubed = Math.Pow(2, 3);  // 2¬≥ = 8.0
Console.WriteLine($"5¬≤ = {squared}");
Console.WriteLine($"2¬≥ = {cubed}");

// SQUARE ROOT
double sqrt = Math.Sqrt(16);  // 4.0
double sqrt2 = Math.Sqrt(2);  // 1.414...
Console.WriteLine($"‚àö16 = {sqrt}");

// ABSOLUTE VALUE (remove negative)
int negative = -42;
int positive = Math.Abs(negative);  // 42
Console.WriteLine($"|-42| = {positive}");

// ROUNDING
double price = 19.67;
double rounded = Math.Round(price);  // 20.0
double ceil = Math.Ceiling(price);  // 20.0 (always up)
double floor = Math.Floor(price);  // 19.0 (always down)
Console.WriteLine($"Round: {rounded}");
Console.WriteLine($"Ceiling: {ceil}");
Console.WriteLine($"Floor: {floor}");

// MIN / MAX
int a = 10, b = 25;
int smaller = Math.Min(a, b);  // 10
int larger = Math.Max(a, b);  // 25
Console.WriteLine($"Min: {smaller}, Max: {larger}");

// CONSTANTS
Console.WriteLine($"œÄ = {Math.PI}");  // 3.14159...
Console.WriteLine($"e = {Math.E}");  // 2.71828...

// TRIGONOMETRY (radians!)
double angle = Math.PI / 2;  // 90 degrees in radians
double sinValue = Math.Sin(angle);  // 1.0
Console.WriteLine($"sin(90¬∞) = {sinValue}");

// RANDOM NUMBERS
Random random = new Random();
int dice = random.Next(1, 7);  // 1 to 6 (7 excluded!)
int percent = random.Next(0, 101);  // 0 to 100
double decimal = random.NextDouble();  // 0.0 to 0.999...
Console.WriteLine($"Dice roll: {dice}");
Console.WriteLine($"Random %: {percent}");
Console.WriteLine($"Random decimal: {decimal}");

// PRACTICAL: Distance formula
double x1 = 0, y1 = 0;
double x2 = 3, y2 = 4;
double distance = Math.Sqrt(Math.Pow(x2 - x1, 2) + Math.Pow(y2 - y1, 2));
Console.WriteLine($"Distance: {distance}");  // 5.0

