challenge.solutionCode:
// Circle Calculator

Console.WriteLine("Enter circle radius:");
double radius = double.Parse(Console.ReadLine());

// Calculate properties
double diameter = 2 * radius;
double circumference = 2 * Math.PI * radius;
double area = Math.PI * Math.Pow(radius, 2);

// Round to 2 decimal places
diameter = Math.Round(diameter, 2);
circumference = Math.Round(circumference, 2);
area = Math.Round(area, 2);

// Display results
Console.WriteLine($"\nCircle with radius {radius}:");
Console.WriteLine($"Diameter: {diameter}");
Console.WriteLine($"Circumference: {circumference}");
Console.WriteLine($"Area: {area}");

// Random circle
Random rnd = new Random();
int randomRadius = rnd.Next(1, 21);  // 1 to 20
double randomArea = Math.PI * Math.Pow(randomRadius, 2);
randomArea = Math.Round(randomArea, 2);

Console.WriteLine($"\nRandom circle (r={randomRadius}): Area = {randomArea}");
