challenge.solutionCode:
// Movie Ticket Calculator

Console.WriteLine("Enter your age:");
int age = int.Parse(Console.ReadLine());

Console.WriteLine("Enter day (weekday or weekend):");
string day = Console.ReadLine();

int price = 15;  // Base price

// Check for child discount
if (age < 12)
{
    price = 8;
    Console.WriteLine("Child discount applied!");
}

// Check for senior discount
if (age >= 65)
{
    price = 10;
    Console.WriteLine("Senior discount applied!");
}

// Check for weekday discount
if (day == "weekday")
{
    price = price - 3;
    Console.WriteLine("Weekday discount: -$3");
}

// Display final price
Console.WriteLine($"\nFinal ticket price: ${price}");
