coderExample:
int temperature = 75;

// if-else: Two paths
if (temperature > 80)
{
    Console.WriteLine("It's hot! Stay hydrated.");
}
else
{
    Console.WriteLine("Nice weather!");
}

// if-else if-else: Multiple paths
int score = 85;

if (score >= 90)
{
    Console.WriteLine("Grade: A");
}
else if (score >= 80)
{
    Console.WriteLine("Grade: B");
}
else if (score >= 70)
{
    Console.WriteLine("Grade: C");
}
else
{
    Console.WriteLine("Grade: F");
}

