challenge.solutionCode:
// BMI Calculator

Console.WriteLine("Enter weight (kg):");
double weight = double.Parse(Console.ReadLine());

Console.WriteLine("Enter height (meters):");
double height = double.Parse(Console.ReadLine());

// Calculate BMI
double bmi = weight / (height * height);
Console.WriteLine($"\nBMI: {bmi:F2}");

// Categorize with else if
if (bmi < 18.5)
{
    Console.WriteLine("Category: Underweight");
    Console.WriteLine("Tip: Consider eating more nutrient-rich foods");
}
else if (bmi < 25)
{
    Console.WriteLine("Category: Normal weight");
    Console.WriteLine("Tip: Keep up the good work!");
}
else if (bmi < 30)
{
    Console.WriteLine("Category: Overweight");
    Console.WriteLine("Tip: Consider regular exercise and balanced diet");
}
else
{
    Console.WriteLine("Category: Obese");
    Console.WriteLine("Tip: Consult with a healthcare provider");
}
