// EQUALITY OPERATORS
int a = 10;
int b = 10;
int c = 20;

bool same = (a == b);  // true (10 equals 10)
bool different = (a != c);  // true (10 not equal 20)
bool wrong = (a == c);  // false (10 not equal 20)

Console.WriteLine($"{a} == {b}: {same}");
Console.WriteLine($"{a} != {c}: {different}");

// RELATIONAL OPERATORS
int age = 25;

bool isAdult = (age >= 18);  // true (25 >= 18)
bool isTeen = (age < 20);  // false (25 not < 20)
bool canDrive = (age > 16);  // true (25 > 16)

Console.WriteLine($"Is adult: {isAdult}");
Console.WriteLine($"Is teen: {isTeen}");

// IN IF STATEMENTS
int score = 85;

if (score >= 60)
{
    Console.WriteLine("Passed!");
}

if (score == 100)
{
    Console.WriteLine("Perfect!");
}

if (score != 0)
{
    Console.WriteLine("You tried!");
}

// STRING COMPARISONS
string password = "secret";

if (password == "secret")
{
    Console.WriteLine("Correct password!");
}

string name1 = "Alice";
string name2 = "Bob";

if (name1 != name2)
{
    Console.WriteLine("Different names");
}

// COMPARING VARIABLES
int playerScore = 100;
int highScore = 95;

if (playerScore > highScore)
{
    Console.WriteLine("New high score!");
    highScore = playerScore;  // Update high score
}

// BOUNDARIES
int temperature = 25;

if (temperature >= 20 && temperature <= 30)
{
    Console.WriteLine("Comfortable temperature!");
}

// PRACTICAL: Age verification
Console.WriteLine("Enter your age:");
int userAge = int.Parse(Console.ReadLine());

if (userAge >= 18)
{
    Console.WriteLine("Access granted");
}
else
{
    Console.WriteLine("Access denied - must be 18+");
}

// COMMON COMPARISONS
int stock = 5;

if (stock <= 0)
{
    Console.WriteLine("Out of stock!");
}
else if (stock < 10)
{
    Console.WriteLine("Low stock warning");
}
else
{
    Console.WriteLine($"In stock: {stock} items");
}

