coderExample:
// Switch statement example
int dayNumber = 3;

switch (dayNumber)
{
    case 1:
        Console.WriteLine("Monday - Back to work!");
        break;
    case 2:
        Console.WriteLine("Tuesday - Still going...");
        break;
    case 3:
        Console.WriteLine("Wednesday - Midweek!");
        break;
    case 4:
        Console.WriteLine("Thursday - Almost there!");
        break;
    case 5:
        Console.WriteLine("Friday - Weekend incoming!");
        break;
    case 6:
    case 7:
        Console.WriteLine("Weekend! Relax!");
        break;
    default:
        Console.WriteLine("Invalid day number!");
        break;
}

// String switch (C# can switch on strings too!)
string command = "jump";

switch (command)
{
    case "jump":
        Console.WriteLine("Player jumps!");
        break;
    case "run":
        Console.WriteLine("Player runs!");
        break;
    case "attack":
        Console.WriteLine("Player attacks!");
        break;
    default:
        Console.WriteLine("Unknown command!");
        break;
}

