// AND OPERATOR (&&) - Both must be true
int age = 25;
bool hasLicense = true;

if (age >= 18 && hasLicense)
{
    Console.WriteLine("Can drive!");  // Runs (both true)
}

int score = 75;
if (score >= 60 && score <= 100)
{
    Console.WriteLine("Valid score in range");  // Runs
}

// OR OPERATOR (||) - At least one must be true
bool isWeekend = false;
bool isHoliday = true;

if (isWeekend || isHoliday)
{
    Console.WriteLine("No work today!");  // Runs (one is true)
}

string role = "admin";
if (role == "admin" || role == "moderator")
{
    Console.WriteLine("Has elevated privileges");  // Runs
}

// NOT OPERATOR (!) - Flips true/false
bool isRaining = false;

if (!isRaining)
{
    Console.WriteLine("No umbrella needed!");  // Runs (!false = true)
}

bool hasAccount = true;
if (!hasAccount)
{
    Console.WriteLine("Please sign up");  // Doesn't run
}

// COMBINING OPERATORS
int temp = 25;
bool isSunny = true;

if ((temp > 20 && temp < 30) && isSunny)
{
    Console.WriteLine("Perfect beach weather!");
}

// PRACTICAL: Login validation
string username = "admin";
string password = "pass123";

if (username == "admin" && password == "pass123")
{
    Console.WriteLine("âœ… Login successful!");
}
else
{
    Console.WriteLine("âŒ Invalid credentials");
}

// PRACTICAL: Discount eligibility
int purchaseAmount = 150;
bool isMember = true;

if (purchaseAmount >= 100 || isMember)
{
    Console.WriteLine("10% discount applied!");
}

// PRACTICAL: Access control
int userAge = 16;
bool hasParentPermission = false;

if (userAge >= 18 || (userAge >= 13 && hasParentPermission))
{
    Console.WriteLine("Access granted");
}
else
{
    Console.WriteLine("Access denied");
}

// SHORT-CIRCUIT EVALUATION
int x = 0;
if (x != 0 && 10 / x > 2)  // Safe! Second part never runs if x == 0
{
    Console.WriteLine("Result greater than 2");
}

// COMPLEX EXAMPLE: Ticket pricing
int visitorAge = 65;
bool isStudent = false;
bool isWeekday = true;
int basePrice = 20;

if (visitorAge < 12 || visitorAge >= 65)
{
    basePrice = basePrice / 2;  // 50% off for kids/seniors
}

if (isStudent && isWeekday)
{
    basePrice = basePrice - 5;  // $5 student weekday discount
}

Console.WriteLine($"Ticket price: ${basePrice}");

