challenge.solutionCode:
// Coffee Shop

Console.WriteLine("=== Coffee Shop ===");
Console.WriteLine("1 - Espresso ($3)");
Console.WriteLine("2 - Latte ($4)");
Console.WriteLine("3 - Cappuccino ($4)");
Console.WriteLine("4 - Americano ($3)");
Console.WriteLine("5 - Mocha ($5)");

Console.WriteLine("\nEnter choice (1-5):");
int choice = int.Parse(Console.ReadLine());

Console.WriteLine("Enter size (S/M/L):");
string size = Console.ReadLine().ToUpper();

string drink = "";
int price = 0;

// Determine drink and base price
switch (choice)
{
    case 1:
        drink = "Espresso";
        price = 3;
        break;
    case 2:
        drink = "Latte";
        price = 4;
        break;
    case 3:
        drink = "Cappuccino";
        price = 4;
        break;
    case 4:
        drink = "Americano";
        price = 3;
        break;
    case 5:
        drink = "Mocha";
        price = 5;
        break;
    default:
        Console.WriteLine("Invalid choice!");
        return;  // Exit program
}

// Add size surcharge
string sizeText = "";
switch (size)
{
    case "S":
        sizeText = "Small";
        // No extra charge
        break;
    case "M":
        sizeText = "Medium";
        price += 1;
        break;
    case "L":
        sizeText = "Large";
        price += 2;
        break;
    default:
        sizeText = "Regular";
        break;
}

// Display order
Console.WriteLine($"\nOrder: {sizeText} {drink}");
Console.WriteLine($"Price: ${price}");
Console.WriteLine("\nThank you! Enjoy your coffee! â˜•");
