// Create a countdown from 10 to 1
// Remember: for (start; condition; change)

// After the loop, display "Liftoff!"

challenge.solutionCode:
// Create a countdown from 10 to 1
for (int i = 10; i >= 1; i--)
{
    Console.WriteLine(i);
}

// After the loop, display "Liftoff!"
Console.WriteLine("Liftoff!");
