challenge.solutionCode:
// Compound Interest Calculator

Console.WriteLine("Enter initial investment:");
double principal = double.Parse(Console.ReadLine());

Console.WriteLine("Enter annual interest rate (%):");
double rate = double.Parse(Console.ReadLine());

Console.WriteLine("Enter number of years:");
int years = int.Parse(Console.ReadLine());

double amount = principal;

Console.WriteLine($"\nStarting amount: ${principal:F2}");
Console.WriteLine($"Interest rate: {rate}%\n");

// Calculate compound interest year by year
for (int year = 1; year <= years; year++)
{
    amount = amount * (1 + rate / 100);
    Console.WriteLine($"Year {year}: ${amount:F2}");
}

// Calculate total interest earned
double interest = amount - principal;

Console.WriteLine($"\nFinal amount: ${amount:F2}");
Console.WriteLine($"Total interest earned: ${interest:F2}");
