// BASIC NESTED LOOP - GRID
for (int row = 0; row < 3; row++)
{
    for (int col = 0; col < 4; col++)
    {
        Console.Write("* ");
    }
    Console.WriteLine();  // New line after each row
}
// Output:
// * * * *
// * * * *
// * * * *

// MULTIPLICATION TABLE
for (int i = 1; i <= 5; i++)
{
    for (int j = 1; j <= 5; j++)
    {
        int result = i * j;
        Console.Write($"{result,4}");  // 4 spaces wide
    }
    Console.WriteLine();
}
// Output:
//    1   2   3   4   5
//    2   4   6   8  10
//    3   6   9  12  15
//    4   8  12  16  20
//    5  10  15  20  25

// TRIANGLE PATTERN
for (int row = 1; row <= 5; row++)
{
    for (int col = 1; col <= row; col++)
    {
        Console.Write("*");
    }
    Console.WriteLine();
}
// Output:
// *
// **
// ***
// ****
// *****

// NUMBER PYRAMID
for (int i = 1; i <= 4; i++)
{
    for (int j = 1; j <= i; j++)
    {
        Console.Write(j);
    }
    Console.WriteLine();
}
// Output:
// 1
// 12
// 123
// 1234

// COMPARING ALL PAIRS
string[] team1 = { "Alice", "Bob", "Charlie" };
string[] team2 = { "Diana", "Eve" };

Console.WriteLine("All possible matchups:");
foreach (string player1 in team1)
{
    foreach (string player2 in team2)
    {
        Console.WriteLine($"{player1} vs {player2}");
    }
}
// Output:
// Alice vs Diana
// Alice vs Eve
// Bob vs Diana
// Bob vs Eve
// Charlie vs Diana
// Charlie vs Eve

// PRACTICAL: Seating chart
int rows = 4;
int seatsPerRow = 5;
int seatNumber = 1;

for (int r = 1; r <= rows; r++)
{
    Console.Write($"Row {r}: ");
    for (int s = 1; s <= seatsPerRow; s++)
    {
        Console.Write($"[{seatNumber}] ");
        seatNumber++;
    }
    Console.WriteLine();
}

// PRACTICAL: Times table quiz
for (int num = 2; num <= 5; num++)
{
    Console.WriteLine($"\n=== {num} Times Table ===");
    for (int mult = 1; mult <= 10; mult++)
    {
        int answer = num * mult;
        Console.WriteLine($"{num} × {mult} = {answer}");
    }
}

// PRACTICAL: Finding duplicates
int[] numbers = { 1, 2, 3, 2, 4, 1, 5 };

Console.WriteLine("Checking for duplicates:");
for (int i = 0; i < numbers.Length; i++)
{
    for (int j = i + 1; j < numbers.Length; j++)
    {
        if (numbers[i] == numbers[j])
        {
            Console.WriteLine($"Duplicate found: {numbers[i]}");
        }
    }
}

// NESTED LOOP WITH BREAK
Console.WriteLine("\nSearching 2D array:");
int[,] grid = { { 1, 2, 3 }, { 4, 5, 6 }, { 7, 8, 9 } };
int searchFor = 5;
bool found = false;

for (int r = 0; r < 3; r++)
{
    for (int c = 0; c < 3; c++)
    {
        if (grid[r, c] == searchFor)
        {
            Console.WriteLine($"Found {searchFor} at [{r},{c}]");
            found = true;
            break;  // Breaks inner loop only!
        }
    }
    if (found) break;  // Need this to break outer loop too!
}

