coderExample:
// Simple nested loop - multiplication table
for (int row = 1; row <= 3; row++)
{
    for (int col = 1; col <= 3; col++)
    {
        Console.WriteLine(row + " x " + col + " = " + (row * col));
    }
    Console.WriteLine("---");  // Separator between rows
}

// Pattern printing - right triangle
for (int i = 1; i <= 5; i++)
{
    for (int j = 1; j <= i; j++)
    {
        Console.Write("* ");
    }
    Console.WriteLine();  // New line after each row
}
// Output:
// *
// * *
// * * *
// * * *
// * * * * *

// Grid coordinates
for (int x = 0; x < 3; x++)
{
    for (int y = 0; y < 3; y++)
    {
        Console.WriteLine("Position: (" + x + ", " + y + ")");
    }
}

