challenge.solutionCode:
// Times table generator
// Outer loop for rows
for (int row = 1; row <= 5; row++)
{
    // Inner loop for columns
    for (int col = 1; col <= 5; col++)
    {
        // Display the multiplication
        Console.WriteLine(row + " x " + col + " = " + (row * col));
    }

    // Blank line between rows
    Console.WriteLine();
}
