challenge.solutionCode:
// Student Grade Manager

Console.WriteLine("How many students?");
int count = int.Parse(Console.ReadLine());

string[] names = new string[count];
int[] scores = new int[count];

// Get student data
for (int i = 0; i < count; i++)
{
    Console.WriteLine($"\nStudent {i + 1} name:");
    names[i] = Console.ReadLine();

    Console.WriteLine($"Student {i + 1} score:");
    scores[i] = int.Parse(Console.ReadLine());
}

// Display all students
Console.WriteLine("\n=== RESULTS ===");
for (int i = 0; i < count; i++)
{
    Console.WriteLine($"{names[i]}: {scores[i]}");
}

// Calculate average
int sum = 0;
for (int i = 0; i < scores.Length; i++)
{
    sum += scores[i];
}
double average = (double)sum / count;
Console.WriteLine($"\nClass average: {average:F1}");

// Find highest
int highestScore = scores[0];
string highestStudent = names[0];
for (int i = 1; i < count; i++)
{
    if (scores[i] > highestScore)
    {
        highestScore = scores[i];
        highestStudent = names[i];
    }
}
Console.WriteLine($"Highest: {highestStudent} ({highestScore})");

// Find lowest
int lowestScore = scores[0];
string lowestStudent = names[0];
for (int i = 1; i < count; i++)
{
    if (scores[i] < lowestScore)
    {
        lowestScore = scores[i];
        lowestStudent = names[i];
    }
}
Console.WriteLine($"Lowest: {lowestStudent} ({lowestScore})");

// Count passed
int passedCount = 0;
for (int i = 0; i < scores.Length; i++)
{
    if (scores[i] >= 60)
    {
        passedCount++;
    }
}
Console.WriteLine($"Passed: {passedCount}/{count}");
