// CREATING LISTS
using System.Collections.Generic;  // Need this!

// Empty list
List<string> names = new List<string>();

// Initialize with values
List<int> scores = new List<int> { 85, 92, 78, 90 };

// ADDING ITEMS
names.Add("Alice");  // First item
names.Add("Bob");    // Second item
names.Add("Charlie");  // Third item
// List grows automatically!

Console.WriteLine($"Count: {names.Count}");  // 3

// ACCESSING ITEMS (like array)
Console.WriteLine(names[0]);  // "Alice"
Console.WriteLine(names[1]);  // "Bob"

// MODIFYING ITEMS
names[1] = "Robert";  // Change "Bob" to "Robert"

// REMOVING ITEMS
names.Remove("Alice");  // Removes "Alice"
names.RemoveAt(0);  // Removes item at index 0

// INSERT AT POSITION
List<string> fruits = new List<string> { "Apple", "Orange" };
fruits.Insert(1, "Banana");  // Insert at index 1
// Now: ["Apple", "Banana", "Orange"]

// CONTAINS - Check if exists
if (fruits.Contains("Banana"))
{
    Console.WriteLine("We have bananas!");
}

// CLEAR - Remove all
fruits.Clear();
Console.WriteLine($"Count after clear: {fruits.Count}");  // 0

// LOOPING THROUGH LIST
List<int> numbers = new List<int> { 10, 20, 30, 40 };

for (int i = 0; i < numbers.Count; i++)
{
    Console.WriteLine($"numbers[{i}] = {numbers[i]}");
}

foreach (int num in numbers)
{
    Console.WriteLine(num);
}

// PRACTICAL: Shopping list
List<string> shopping = new List<string>();

// Add items
shopping.Add("Milk");
shopping.Add("Bread");
shopping.Add("Eggs");
shopping.Add("Butter");

Console.WriteLine($"Shopping list has {shopping.Count} items:");
foreach (string item in shopping)
{
    Console.WriteLine($"- {item}");
}

// Remove item (bought it!)
shopping.Remove("Milk");
Console.WriteLine($"\nAfter buying milk: {shopping.Count} items left");

// PRACTICAL: High scores tracker
List<int> highScores = new List<int> { 100, 95, 87, 92 };

Console.WriteLine("Enter new score:");
int newScore = int.Parse(Console.ReadLine());

highScores.Add(newScore);
highScores.Sort();  // Sort ascending
highScores.Reverse();  // Now descending

Console.WriteLine("\nTop 3 scores:");
for (int i = 0; i < Math.Min(3, highScores.Count); i++)
{
    Console.WriteLine($"{i + 1}. {highScores[i]}");
}

// PRACTICAL: Todo list
List<string> todos = new List<string>();

while (true)
{
    Console.WriteLine("\n1. Add task");
    Console.WriteLine("2. Complete task");
    Console.WriteLine("3. View tasks");
    Console.WriteLine("4. Exit");

    string choice = Console.ReadLine();

    if (choice == "1")
    {
        Console.WriteLine("Enter task:");
        todos.Add(Console.ReadLine());
    }
    else if (choice == "2" && todos.Count > 0)
    {
        Console.WriteLine("Task number to complete:");
        int index = int.Parse(Console.ReadLine()) - 1;
        todos.RemoveAt(index);
    }
    else if (choice == "3")
    {
        for (int i = 0; i < todos.Count; i++)
        {
            Console.WriteLine($"{i + 1}. {todos[i]}");
        }
    }
    else if (choice == "4")
    {
        break;
    }
}

