challenge.solutionCode:
// Contact Manager

using System.Collections.Generic;

List<string> names = new List<string>();
List<string> phones = new List<string>();

while (true)
{
    Console.WriteLine("\n=== CONTACT MANAGER ===");
    Console.WriteLine("1. Add contact");
    Console.WriteLine("2. Remove contact");
    Console.WriteLine("3. Search contact");
    Console.WriteLine("4. List all");
    Console.WriteLine("5. Exit");

    string choice = Console.ReadLine();

    if (choice == "1")
    {
        // Add contact
        Console.WriteLine("Enter name:");
        string name = Console.ReadLine();
        Console.WriteLine("Enter phone:");
        string phone = Console.ReadLine();

        names.Add(name);
        phones.Add(phone);
        Console.WriteLine("‚úÖ Contact added!");
    }
    else if (choice == "2")
    {
        // Remove contact
        Console.WriteLine("Enter name to remove:");
        string name = Console.ReadLine();

        int index = names.IndexOf(name);
        if (index >= 0)
        {
            names.RemoveAt(index);
            phones.RemoveAt(index);
            Console.WriteLine("‚úÖ Contact removed!");
        }
        else
        {
            Console.WriteLine("‚ùå Contact not found");
        }
    }
    else if (choice == "3")
    {
        // Search contact
        Console.WriteLine("Enter name to search:");
        string name = Console.ReadLine();

        int index = names.IndexOf(name);
        if (index >= 0)
        {
            Console.WriteLine($"Found: {names[index]} - {phones[index]}");
        }
        else
        {
            Console.WriteLine("‚ùå Contact not found");
        }
    }
    else if (choice == "4")
    {
        // List all
        Console.WriteLine($"\nContacts ({names.Count}):");
        for (int i = 0; i < names.Count; i++)
        {
            Console.WriteLine($"{i + 1}. {names[i]} - {phones[i]}");
        }
    }
    else if (choice == "5")
    {
        // Exit
        Console.WriteLine("Goodbye!");
        break;
    }
}
