challenge.solutionCode:
// Student Management System

using System;
using System.Collections.Generic;
using System.Linq;

class Student
{
    public string Name;
    public string StudentID;
    public int Grade;
    public bool IsPassing;
}

class Program
{
    static void Main()
    {
        // Create list of students
        List<Student> students = new List<Student>();

        // Add students
        students.Add(new Student { Name = "Alice", StudentID = "S001", Grade = 85, IsPassing = true });
        students.Add(new Student { Name = "Bob", StudentID = "S002", Grade = 55, IsPassing = false });
        students.Add(new Student { Name = "Charlie", StudentID = "S003", Grade = 78, IsPassing = true });
        students.Add(new Student { Name = "Diana", StudentID = "S004", Grade = 92, IsPassing = true });
        students.Add(new Student { Name = "Eve", StudentID = "S005", Grade = 45, IsPassing = false });

        // Display all students
        Console.WriteLine("=== STUDENT ROSTER ===");
        foreach (Student student in students)
        {
            string status = student.IsPassing ? "PASSING" : "FAILING";
            Console.WriteLine($"{student.Name} ({student.StudentID}): {student.Grade} - {status}");
        }

        // Calculate statistics
        double average = students.Average(s => s.Grade);
        int passingCount = students.Count(s => s.IsPassing);

        Student highest = students.OrderByDescending(s => s.Grade).First();
        Student lowest = students.OrderBy(s => s.Grade).First();

        Console.WriteLine($"\nClass Average: {average:F1}");
        Console.WriteLine($"Passing: {passingCount}/{students.Count} students");
        Console.WriteLine($"Highest: {highest.Name} ({highest.Grade})");
        Console.WriteLine($"Lowest: {lowest.Name} ({lowest.Grade})");

        // Display failing students
        var failingStudents = students.Where(s => !s.IsPassing).ToList();
        Console.WriteLine($"\nFailing students ({failingStudents.Count}):");
        foreach (Student student in failingStudents)
        {
            Console.WriteLine($"- {student.Name} ({student.StudentID}): {student.Grade}");
        }
    }
}
